mapper.update:
UPDATE board_tbl
SET title = #{title}
    , content = #{content}
    , updateId = #{updateId}
    , updateDt = #{updateDt}
WHERE id = #{id}

UPDATE board_tbl
SET deleteId = #{deleteId}
  , deleteDt = #{deleteDt}
  , deleteFlag = #{deleteFlag}
WHERE id = #{id}

mapper.delete:
UPDATE board_tbl
SET deleteFlag = true
WHERE id = #{id}
